import React from "react";


class Register extends React.Component{
	constructor(props){
		super(props);
		this.state = {
			email:'',
			password:'',
			name:'',
		}
	}

	onNameChange = (event) =>{
		this.setState({
			name: event.target.value
		})
	} 

	onEmailChange = (event) =>{
		this.setState({
			email: event.target.value
		})
	} 

	onPasswordChange = (event) =>{
		this.setState({
			password: event.target.value
		})
	} 

	onSubmitSignIn = () => {
		fetch('http://localhost:3000/register' , {
			method: 'post',
			headers: {'Content-Type':'application/json'},
			body: JSON.stringify({
				email: this.state.email,
				password: this.state.password,
				name: this.state.name
			})
		})
		.then(response => response.json())
		.then(user => {
			if(user){
				this.props.loadUser(user)
				this.props.onRouteChange('home');
			}
		})
	}

	render(){
		return(
			<article className="br3 ba b--white-60 mv4 w-100 w-50-m w-25-l mw6 shadow-3 center">
				<main className="pa4 white-80">
					<div className="measure">
					<fieldset id="sign_up" className="ba b--transparent ph0 mh0">
					    <legend className="f1 fw6 ph0 mh0">Register</legend>

					    <div className="lh-copy mt3">
					    <p href="#0" className="f5 white mb4 db">Register will give you the access to your Rank</p>
					   </div>

					    <div className="mt3">
					    <label className="db fw6 lh-copy f6" htmlFor="email-address">Name</label>
					    <input 
					    className="pa2 input-reset ba bg-transparent hover-bg-navy white w-100" 
					    type="text" 
					    name="name"  
					    id="name"
					    onChange={this.onNameChange}
					    />

					    </div>

					    <div className="mt3">
					    <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
					    <input 
					    className="pa2 input-reset ba bg-transparent hover-bg-navy white w-100" 
					    type="email" 
					    name="email-address"  
					    id="email-address" 
					    onChange={this.onEmailChange}
					    />

					    </div>

					    <div className="mv3">
					    <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
					    <input 
					    className="b pa2 input-reset ba bg-transparent hover-bg-navy white w-100" 
					    type="password" 
					    name="password"  
					    id="password" 
					    onChange={this.onPasswordChange}
					    />

					  </div>

					</fieldset>

					<div className="">
					  <input 
					  onClick={this.onSubmitSignIn}
					  className="b ph3 pv2 input-reset white ba b--white bg-transparent grow pointer f6 dib" 
					  type="submit" 
					  value="Register"
					/>
					</div>
					</div>
				</main>
			</article>

		 );
	}

}

export default Register;
